import { FC, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  StatusBar,
} from "react-native";

const Brick: FC<{
  callback: (ind: number) => void;
  index: number;
  turn: { player: number };
}> = ({ callback, index, turn }) => {
  const [player, setPlayer] = useState<number>(0);

  const clickHandler = () => {
    console.log("click Brick");
    setPlayer(turn.player);
    callback(index);
  };

  const backColors = ["white", "red", "yellow"];
  var img = player == 1 ? require('./assets/x_img.png') : require('./assets/circle_img.png')
  return (
    <View style={[styles.brick]}>
      <TouchableOpacity
        onPress={clickHandler}
        style={[styles.brick,]}
      >
        <Image source={img}>
        </Image>
      </TouchableOpacity>
    </View>
  );
};

const App: FC = () => {
  const turn = { player: 1 }; //1: X , 2: O

  const clickHandler = (ind: number) => {
    console.log("click App " + ind);
    if (turn.player == 1) {
      turn.player = 2;
    } else {
      turn.player = 1;
    }
  };

  const reloadHandler = () => {
    console.log("click reloadHandler ");
  };
  console.log("Hello React");
  return (
    <View style={styles.container}>
      <Text style={styles.title}>X Mix Drix</Text>
      <View style={styles.rowContainer}>
        <Brick callback={clickHandler} index={0} turn={turn}></Brick>
        <Brick callback={clickHandler} index={1} turn={turn}></Brick>
        <Brick callback={clickHandler} index={2} turn={turn}></Brick>
      </View>
      <View style={styles.rowContainer}>
        <Brick callback={clickHandler} index={3} turn={turn}></Brick>
        <Brick callback={clickHandler} index={4} turn={turn}></Brick>
        <Brick callback={clickHandler} index={5} turn={turn}></Brick>
      </View>
      <View style={styles.rowContainer}>
        <Brick callback={clickHandler} index={6} turn={turn}></Brick>
        <Brick callback={clickHandler} index={7} turn={turn}></Brick>
        <Brick callback={clickHandler} index={8} turn={turn}></Brick>
      </View>
      <TouchableOpacity onPress={reloadHandler}>
        <Text style={styles.button}>RELOAD</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginTop: StatusBar.currentHeight,
    flex: 1,
    backgroundColor: "blue",
    // justifyContent: 'center',
    // alignItems: 'center'
  },
  rowContainer: {
    flexDirection: "row",
  },
  label: {
    flex: 1,
  },
  child: {
    margin: 5,
    flex: 1,
    aspectRatio: 1,
  },
  brick: {
    backgroundColor: "white",
    margin: 5,
    flex: 1,
    aspectRatio: 1,
  },
  title: {
    fontSize: 30,
    textAlign: "center",
  },
  button: {
    margin: 5,
    fontSize: 30,
    textAlign: "center",
    backgroundColor: "grey",
    borderRadius: 10,
  },
});

export default App;
