import { FC, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  StatusBar,
  Alert,
} from "react-native";

const Brick: FC<{
  callback: (ind: number) => void;
  index: number;
  turn: { player: number };
  mapArray: Array<String>;
}> = ({ callback, index, turn, mapArray }) => {
  const [player, setPlayer] = useState<number>(1);
  const [touched, setTouched] = useState(false);
  const clickHandler = () => {
    console.log("click Brick");
    console.log("touched: " + touched + "|| player: " + player);
    checkImg();

    setPlayer(turn.player);
    setTouched(true);
    callback(index);
  };

  const checkImg = () => {
    if (mapArray[index] != "x" && mapArray[index] != "O") {
      if (player == 1 && touched == true) {
        mapArray[index] = "x";
        return require("./assets/x_img.png");
      } else if (player == 2 && touched == true) {
        mapArray[index] = "O";
        return require("./assets/circle_img.png");
      } else return "bla";
    } else {
      console.log("this already taken");
      Alert.alert("this already taken");
    }
  };

  var img =
    player == 1
      ? require("./assets/x_img.png")
      : require("./assets/circle_img.png");
  return (
    <View style={[styles.brick]}>
      <TouchableOpacity onPress={clickHandler} style={[styles.brick]}>
        {/* {player == 1 && touched == true && (
          <Image
            style={{ width: 100, height: 100, margin: 10 }}
            source={require("./assets/x_img.png")}
          ></Image>
        )}
        {player == 2 && touched == true && (
          <Image
            style={{ width: 100, height: 100, margin: 10 }}
            source={require("./assets/circle_img.png")}
          ></Image>
        )} */}
        <Image
          style={{ width: 100, height: 100, margin: 10 }}
          source={checkImg()}
        ></Image>
      </TouchableOpacity>
    </View>
  );
};

const App: FC = () => {
  const turn = { player: 1 }; //1: X , 2: O

  var map = [
    ["", "", ""], // 1st row
    ["", "", ""], // 2nd row
    ["", "", ""], // 3rd row
  ];

  var mapArray = ["", "", "", "", "", "", "", "", ""];

  const clickHandler = (ind: number) => {
    console.log("click App " + ind);
      if (turn.player == 1) {
        turn.player = 2;
      } else {
        turn.player = 1;
      }

    console.log(mapArray);
  };

  const checkBoard = () => {};

  const reloadHandler = () => {
    console.log("click reloadHandler ");
  };
  console.log("Hello React");
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Tic Tac Toe</Text>
      <View style={styles.rowContainer}>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={0}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={1}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={2}
          turn={turn}
        ></Brick>
      </View>
      <View style={styles.rowContainer}>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={3}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={4}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={5}
          turn={turn}
        ></Brick>
      </View>
      <View style={styles.rowContainer}>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={6}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={7}
          turn={turn}
        ></Brick>
        <Brick
          callback={clickHandler}
          mapArray={mapArray}
          index={8}
          turn={turn}
        ></Brick>
      </View>
      <TouchableOpacity onPress={reloadHandler}>
        <Text style={styles.button}>RELOAD</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginTop: StatusBar.currentHeight,
    flex: 1,
    backgroundColor: "pink",
    // justifyContent: 'center',
    // alignItems: 'center'
  },
  rowContainer: {
    flexDirection: "row",
  },
  label: {
    flex: 1,
  },
  child: {
    margin: 5,
    flex: 1,
    aspectRatio: 1,
  },
  brick: {
    backgroundColor: "white",
    margin: 5,
    flex: 1,
    aspectRatio: 1,
  },
  title: {
    fontSize: 30,
    textAlign: "center",
  },
  button: {
    margin: 5,
    fontSize: 30,
    textAlign: "center",
    backgroundColor: "grey",
    borderRadius: 10,
  },
});

export default App;
